package Lab_Excercises;

public class DefaultValueOfPrimitiveDataType {
	int m ;
	boolean b;
	char c;
	short s;
	byte by;
	double d;
	float f;
	long l;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		DefaultValueOfPrimitiveDataType d = new DefaultValueOfPrimitiveDataType();
		System.out.println("Default Value of Integer:"+d.m);
		System.out.println("Default Value of Boolean:"+d.b);
		System.out.println("Default Value of Byte:"+d.by);
		System.out.println("Default Value of Short:"+d.s);
		System.out.println("Default Value of Float:"+d.f);
		System.out.println("Default Value of Long:"+d.l);
		System.out.println("Default Value of Double:"+d.d);
		System.out.println("Default Value of Character:"+d.c);
	}

}
